import React, { useState, useEffect} from 'react';
import {Link, Redirect} from 'react-router-dom';
import Base from '../core/Base';
import {getProjects} from './helper/coreapicalls'
import {signup, isAuthenticated,addTaskFunc} from '../auth/helper/index'


const AddTask = () =>{
    const [values, setValues] = useState({
        task_name : '',
        task_description : '',
        project : '',
        task_start : '',
        task_end : '',
        all_projects:[],
        error : '',
        success : false,
    });

    useEffect(() => {
      projectGetter(id);
  })

    const {task_name, task_description, project, task_start, task_end, error, success} = values;

    const id = isAuthenticated && isAuthenticated().user.id;

    const handleChange = (name) => (event) =>{
        setValues({...values, error:false, [name]: event.target.value});
    };

    const onSubmit = (event) =>{
        event.preventDefault();
        setValues({...values, error:false});
        addTaskFunc({task_name, task_description, project, task_start, task_end})
        .then((data) => {
            console.log('DATA', data);
            if (data.success){
                setValues({
                    ...values,
                    task_name:'',
                    task_description:'',
                    task_start:'',
                    task_end:'',
                    error:'',
                    success: true
                })
            }else{
                setValues({
                    ...values,
                    error: true,
                    success: false
                })
            }
        })
        .catch((e) => console.log(e));
    };

    const successMessage = () => {
        return (
            <div className = 'row'>
                <div className = 'col-md-6 offset-sm-3 text-left'>
                    <div className = 'alert alert-success' style = {{display : success ? "" : "none"}}>
                        New Task created successfully. <Link to = '/'>Go To Home</Link>
                    </div>
                </div>
            </div>
        );
    };

    const errorMessage = () => {
        return (
            <div className = 'row'>
                <div className = 'col-md-6 offset-sm-3 text-left'>
                    <div className = 'alert alert-danger' style = {{display : error ? "" : "none"}}>
                        Check all fields again
                    </div>
                </div>
            </div>
        );
    };
    
    const projectGetter = (id) =>{
      getProjects(id)
      .then((data) => {
        console.log('DATA', data);
        if (data.success){
            setValues({
                ...values,
                all_projects:data
            })};      
      }).catch((e) => console.log(e))
    };

    const signUpForm = () => {
        return (
            <div className = 'row'>
                <div className ='col-md-6 offset-sm-3 text-left'>
                    <form>
                        <div className = 'form-group'>
                            <label className = 'text-light'>Task Name</label>
                            <input 
                            className = 'form-control'
                            value = {task_name}
                            onChange = {handleChange('task_name')}
                            type = 'text'/>
                        </div>
                        <div className = 'form-group'>
                            <label className = 'text-light'>Task Description</label>
                            <input 
                            className = 'form-control input-lg'
                            value = {task_description}
                            onChange = {handleChange('task_description')}
                            type = 'text'/>
                        </div>
                        <div className = 'form-group'>
                            <label className = 'text-light'>Project</label>
                            <select class="form-select" aria-label="Default select example">
                            {values.all_projects.map((proj) =>{
                              return(
                                <option value={proj.id}>{proj.name}</option>
                              )
                            }) }
                            </select>
                        </div><br/>
                        <button onClick={onSubmit} className = 'btn btn-success btn-block'>Submit</button>
                    </form>
                </div>
            </div>
        )
    };

    return(
      <div>
          {!isAuthenticated() ? (<Redirect to = '/signin' />) :
      (<Base title = 'Sign Up Page' description = 'A signup for Nerdy User'>
          {successMessage()}
          {errorMessage()}
          {signUpForm()}
          <p className = 'text-white text-center'>
              {JSON.stringify(values)}
          </p> 
      </Base>)}
      </div>
    );
}

export default AddTask
