import React, { useState, useEffect } from 'react'
import {getTimeEntry} from './helper/coreapicalls'
import { isAuthenticated } from '../auth/helper';

import Base from './Base';
import Card from './Card';
import '../styles.css';

export default function Home (){
    const [timeEntrys, setTimeEntrys] = useState([]);
    const [filterDate, setFilterDate] = useState(Date.now());
    const [error, setError] = useState(false)

    const id = isAuthenticated && isAuthenticated().user.id

    const loadTimeEntry = (id) => {
        getTimeEntry(id)
        .then(data =>{
            if (data.error) {
                setError(data.error);
                console.log(error);
            }else {
                setTimeEntrys(data);
            }
        }
        );
    };

    useEffect(() => {
        loadTimeEntry(id);
    })


    const SetDateFilter = (event) =>{
        setFilterDate(event.target.value);
    };

    return(
        <Base title = 'Nerdy' description = 'T-shirt store'>
            <h1>
                Home Component
            </h1>
            <div class="form-group row col-4 mb-4 row justify-content-md-center">
            <label for="example-date-input" class="col-2 col-form-label">Select date for task</label>
            <input class="form-control " onChange = {SetDateFilter} type="date" value="2011-08-19" id="date-input"/>
            </div>
            <div className = 'row'>
                {timeEntrys.map((timeEntry) => {
                    return(
                        <div key = {timeEntry.id} className = 'col-12 mb-12'>
                            <Card timeEntry = {timeEntry}/>
                        </div>
                    )
                })}

            </div>
        </Base>
    )
}
